import React, { useState } from "react";
import axios from "axios";
import toast, { Toaster } from "react-hot-toast";
import "../App.css";
import { useNavigate, Link } from "react-router-dom";
import { useForm } from "react-hook-form";

const SignUp = () => {
    const [loader, setLoader] = useState(false);

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const navigate = useNavigate();

    const handleFormSubmit = async (formData) => {
        setLoader(true);
        try {
            const response = await axios.post("http://localhost:3001/api/register", formData);
            console.log(response.data);

            setLoader(false);
            toast.success("You have been registered successfully!");
            setTimeout(() => {
                navigate("/auth/confirmEmail");
            }, 1000);
        } catch (err) {
            setLoader(false);

            if (err.response && err.response.data) {
                const errorResponse = err.response.data;

                if (errorResponse.details) {
                    const validationMessages = errorResponse.details
                        .map((detail) => detail.message)
                        .join(", ");
                    toast.error(`Validation Error: ${validationMessages}`);
                } else if (errorResponse.msg) {
                    toast.error(`Error: ${errorResponse.msg}`);
                }
            } else {
                const generalErrorMessage = `There was an error: ${err.message}`;
                toast.error(generalErrorMessage);
            }

            console.log("Error occurred during form submission:", err);
        }
    };

    return (
        <section className="bg-gray-1 py-20 dark:bg-dark lg:py-[120px]">
            <Toaster position="top-right" reverseOrder={false} />
            <div className="container mx-auto">
                <div className="-mx-4 flex flex-wrap">
                    <div className="w-full px-4">
                        <div className="relative mx-auto max-w-[52%] overflow-hidden rounded-lg bg-white px-10 py-16 text-center dark:bg-dark-2 sm:px-12 md:px-[60px]">
                            <div className="mb-10 text-center md:mb-16">
                                <a href="/#" className="mx-auto inline-block max-w-[160px]">
                                    <img
                                        src="https://cdn.tailgrids.com/2.0/image/assets/images/logo/logo-primary.svg"
                                        alt="logo"
                                    />
                                </a>
                            </div>

                            <form method="POST" onSubmit={handleSubmit(handleFormSubmit)}>
                                <div className="flex gap-4 w-full">
                                    <div className="w-full">
                                        <input
                                            type="text"
                                            placeholder="Name"
                                            {...register("name", {
                                                required: "Name is required",
                                                pattern: {
                                                    value: /^[a-zA-Z0-9_-]+$/,
                                                    message: "Name can only include letters, numbers, underscores, and hyphens",
                                                },
                                            })}
                                            className="input-box"
                                        />
                                        {errors.name && (
                                            <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                                {errors.name.message}
                                            </div>
                                        )}
                                    </div>
                                    <div className="w-full">
                                        <input
                                            type="tel"
                                            placeholder="Number"
                                            {...register("number", {
                                                required: "Number is required",
                                                pattern: {
                                                    value: /^[0-9]+$/,
                                                    message: "Number can only contain digits",
                                                },
                                            })}
                                            className="input-box"
                                        />
                                        {errors.number && (
                                            <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                                {errors.number.message}
                                            </div>
                                        )}
                                    </div>
                                </div>

                                <input
                                    type="text"
                                    placeholder="Address"
                                    {...register("address", {
                                        required: "Address is required",
                                        pattern: {
                                            value: /^[a-zA-Z0-9\s,-]+$/,
                                            message: "Address can only include letters, numbers, spaces, commas, and hyphens",
                                        },
                                    })}
                                    className="input-box"
                                />
                                {errors.address && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.address.message}
                                    </div>
                                )}

                                <input
                                    type="email"
                                    placeholder="Email"
                                    {...register("email", {
                                        required: "Email is required",
                                        pattern: {
                                            value: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+\.[a-zA-Z0-9-]+$/,
                                            message: "Please enter a valid email address",
                                        },
                                    })}
                                    className="input-box"
                                />
                                {errors.email && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.email.message}
                                    </div>
                                )}

                                <input
                                    type="password"
                                    placeholder="Password"
                                    {...register("password", {
                                        required: "Password is required",
                                        minLength: {
                                            value: 6,
                                            message: "Password must be at least 6 characters long",
                                        },
                                    })}
                                    className="input-box"
                                />
                                {errors.password && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.password.message}
                                    </div>
                                )}

                                <div className="mb-10">
                                    <button
                                        type="submit"
                                        disabled={loader}
                                        className="w-full cursor-pointer rounded-md border border-primary bg-primary px-5 py-3 text-base font-medium text-white transition hover:bg-opacity-90 bg-[#090e34]"
                                    >
                                        {loader ? (
                                            <img
                                                src="https://cdn.pixabay.com/animation/2023/05/02/04/29/04-29-06-428_512.gif"
                                                alt="Loading..."
                                                className="loading-gif"
                                            />
                                        ) : (
                                            "Submit"
                                        )}
                                    </button>
                                </div>
                            </form>

                            <p className="mb-6 text-base text-secondary-color dark:text-dark-7">Connect With</p>
                            <ul className="-mx-2 mb-12 flex justify-between">
                                {/* Social media buttons here */}
                            </ul>

                            <Link to="/auth/login" className="text-blue-500 hover:underline">
                                Already have an account? Log In
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default SignUp;

const InputBox = ({ type, placeholder, name, value,onChange }) => {
    return (

        <div className="mb-4 w-full">
            <input
                type={type}
                placeholder={placeholder}
                name={name}
                value={value}
                onChange={onChange}
                className="w-full rounded-md border border-stroke bg-transparent px-5 py-3 text-base text-body-color outline-none focus:border-primary focus-visible:shadow-none dark:border-dark-3 dark:text-black"
            />
        </div>
    );
};
