import React, { useState } from "react";
import axios from "axios";
import toast, { Toaster } from "react-hot-toast";
import "../App.css";
import { useNavigate, Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import InputBox from "./InputBox"; // Ensure you have this component defined or imported

const SignUp = () => {
    const [loader, setLoader] = useState(false);
    const { register, handleSubmit, formState: { errors } } = useForm();
    const navigate = useNavigate();

    const handleFormSubmit = async (formData) => {
        setLoader(true);

        try {
            const response = await axios.post('http://localhost:3001/api/register', formData);
            setLoader(false);
            toast.success("You have been registered successfully!");
            setTimeout(() => {
                navigate('/auth/confirmEmail');
            }, 1000);
        } catch (err) {
            setLoader(false);
            if (err.response && err.response.data) {
                const errorResponse = err.response.data;
                if (errorResponse.details) {
                    const validationMessages = errorResponse.details.map((detail) => detail.message).join(', ');
                    toast.error(`Validation Error: ${validationMessages}`);
                } else if (errorResponse.msg) {
                    toast.error(`Error: ${errorResponse.msg}`);
                }
            } else {
                toast.error(`There was an error: ${err.message}`);
            }
        }
    };

    return (
        <section className="bg-gray-1 py-20 dark:bg-dark lg:py-[120px]">
            <Toaster position="top-right" reverseOrder={false} />
            <div className="container mx-auto">
                <div className="-mx-4 flex flex-wrap">
                    <div className="w-full px-4">
                        <div className="relative mx-auto max-w-[52%] overflow-hidden rounded-lg bg-white px-10 py-16 text-center dark:bg-dark-2 sm:px-12 md:px-[60px]">
                            <div className="mb-10 text-center md:mb-16">
                                <Link to="/" className="mx-auto inline-block max-w-[160px]">
                                    <img src="https://cdn.tailgrids.com/2.0/image/assets/images/logo/logo-primary.svg" alt="logo" />
                                </Link>
                            </div>

                            <form method="POST" onSubmit={handleSubmit(handleFormSubmit)}>
                                <div className="flex gap-4 w-full">
                                    <div className="w-full">
                                        <InputBox
                                            type="text"
                                            placeholder="Name"
                                            name="name"
                                            {...register("name", {
                                                required: "Name is required",
                                                pattern: /^[a-z0-9_-]+$/,
                                            })}
                                        />
                                        {errors.name && (
                                            <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                                {errors.name.message}
                                            </div>
                                        )}
                                    </div>
                                    <div className="w-full">
                                        <InputBox
                                            type="tel"
                                            placeholder="Number"
                                            name="number"
                                            {...register("number", {
                                                required: "Number is required",
                                                pattern: /^[0-9]+$/,
                                            })}
                                        />
                                        {errors.number && (
                                            <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                                {errors.number.message}
                                            </div>
                                        )}
                                    </div>
                                </div>

                                <InputBox
                                    type="text"
                                    placeholder="Address"
                                    name="address"
                                    {...register("address", {
                                        required: "Address is required",
                                        pattern: /^[a-z0-9_-]+$/,
                                    })}
                                />
                                {errors.address && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.address.message}
                                    </div>
                                )}

                                <InputBox
                                    type="email"
                                    placeholder="Email"
                                    name="email"
                                    {...register("email", {
                                        required: "Email is required",
                                        pattern: /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
                                    })}
                                />
                                {errors.email && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.email.message}
                                    </div>
                                )}

                                <InputBox
                                    type="password"
                                    placeholder="Password"
                                    name="password"
                                    {...register("password", {
                                        required: "Password is required",
                                        pattern: /^[A-Za-z0-9-]+$/,
                                    })}
                                />
                                {errors.password && (
                                    <div className="text-red-500 p-2 mt-1 text-sm text-start">
                                        {errors.password.message}
                                    </div>
                                )}

                                <div className="mb-10">
                                    <button
                                        type="submit"
                                        disabled={loader}
                                        className="w-full cursor-pointer rounded-md border border-primary bg-primary px-5 py-3 text-base font-medium text-white transition hover:bg-opacity-90 bg-[#090e34]"
                                    >
                                        {loader ? (
                                            <img
                                                src="https://cdn.pixabay.com/animation/2023/05/02/04/29/04-29-06-428_512.gif"
                                                alt="Loading..."
                                                className="loading-gif"
                                            />
                                        ) : (
                                            "Submit"
                                        )}
                                    </button>
                                </div>
                            </form>

                            <p className="mb-6 text-base text-secondary-color dark:text-dark-7">
                                Connect With
                            </p>
                            <ul className="-mx-2 mb-12 flex justify-between">
                                <li className="w-full px-2">
                                    <a href="/#" className="flex h-11 items-center justify-center rounded-md bg-[#4064AC] hover:bg-opacity-90">
                                        <svg width="10" height="20" viewBox="0 0 10 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                                            <path d="M9.29878 8H7.74898H7.19548V7.35484V5.35484V4.70968H7.74898H8.91133C9.21575 4.70968 9.46483 4.45161 9.46483 4.06452V0.645161C9.46483 0.290323 9.24343 0 8.91133 0H6.89106C4.70474 0 3.18262 1.80645 3.18262 4.48387V7.29032V7.93548H2.62912H0.747223C0.359774 7.93548 0 8.29032 0 8.80645V11.129C0 11.5806 0.304424 12 0.747223 12H2.57377H3.12727V12.6452V19.129C3.12727 19.5806 3.43169 20 3.87449 20H6.47593C6.64198 20 6.78036 19.9032 6.89106 19.7742C7.00176 19.6452 7.08478 19.4194 7.08478 19.2258V12.6774V12.0323H7.66596H8.91133C9.2711 12.0323 9.54785 11.7742 9.6032 11.3871V11.3548V11.3226L9.99065 9.09677C10.0183 8.87097 9.99065 8.6129 9.8246 8.35484C9.76925 8.19355 9.52018 8.03226 9.29878 8Z" fill="white" />
                                        </svg>
                                    </a>
                                </li>
                                {/* Additional social login buttons */}
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default SignUp;
